import { promisify } from "util";
import unirest from "unirest";
import redis from "redis";
import config from "../config";

const cache = redis.createClient();
const getAsync = promisify(cache.get).bind(cache);
const setAsync = promisify(cache.setex).bind(cache);
const getTimeLeft = promisify(cache.ttl).bind(cache);

const createNewsApi = (category) => {
  const api = unirest("GET", config.newsUri);

  api.query({
    category,
    region: "US",
  });

  api.headers({
    "x-rapidapi-key": config.rapidApiKey,
    "x-rapidapi-host": config.rapidApiHost,
    useQueryString: true,
  });

  return api;
};

const createAnalysisApi = (symbol) => {
  const api = unirest("GET", config.analysisUri);

  api.query({
    symbol,
    region: "US",
  });

  api.headers({
    "x-rapidapi-key": config.rapidApiKey,
    "x-rapidapi-host": config.rapidApiHost,
    useQueryString: true,
  });

  return api;
};

export const updateNews = (key, category) => {
  const api = createNewsApi(category);

  api.end((response) => {
    if (response.error) {
      console.error("error");
    }

    const values = response.body.items.result;
    setAsync(key, 60 * 5, JSON.stringify(values));
  });
};

export const updateAnalysisApi = (key, symbol) => {
  const api = createAnalysisApi(symbol);

  api.end((response) => {
    if (response.error) {
      console.error("error");
    }

    const values = response.body.items.result;
    setAsync(key, 60 * 5, JSON.stringify(values));
  });
};

export const getNews = async (req, res) => {
  const { category } = req.query;
  const key = `finance::news::${category}`;

  const values = await getAsync(key);
  if (values) {
    const timeLeft = await getTimeLeft(key);
    if (timeLeft < 30) {
      updateNews(key, category);
    }

    return res.send({ data: JSON.parse(values) });
  }

  const api = createNewsApi(category);
  api.end((response) => {
    if (response.error) {
      next(response.error);
    }

    const values = response.body.items.result;
    setAsync(key, 60 * 5, JSON.stringify(values));

    return res.send({ data: values });
  });
};

export const getAnalysis = async (req, res) => {
  const { symbol } = req.query;
  const key = `finance::analysis::${symbol}`;

  const values = await getAsync(key);
  if (values) {
    const timeLeft = await getTimeLeft(key);
    if (timeLeft < 30) {
      updateAnalysisApi(key, symbol);
    }

    return res.send({ data: JSON.parse(values) });
  }

  const api = createAnalysisApi(category);
  api.end((response) => {
    if (response.error) {
      next(response.error);
    }

    const values = response.body.items.result;
    setAsync(key, 60 * 5, JSON.stringify(values));

    return res.send({ data: values });
  });
};

